rego_modules.security.rego:
package guardrails

deny[v] {
  input.target.resolved_config.auth.mfa != true
  v := {"id":"mfa_required","message":"MFA must be enabled","path":"/auth/mfa","target": input.target.name}
}

warn[v] {
  input.target.resolved_config.auth.session_timeout > 3600
  v := {"id":"long_session","message":"Session timeout > 1 hour","path":"/auth/session_timeout","target": input.target.name}
}
